	z-index: 0;	

	&::before {
		display: block;
		position: absolute;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
		border: @xf-borderSize solid @xf-xtr_breadcrumb--border-color;
		border-radius: inherit;
		pointer-events: none;
		z-index: 2;
	}

	> li {
		margin: 0;
		font-size: inherit;
		padding-left: 15px;

		&:first-child {
			border-top-left-radius: inherit;
			border-bottom-left-radius: inherit;
			span {
				&:before {
					.m-faBase();
					text-rendering: auto;
					-webkit-font-smoothing: antialiased;
					-moz-osx-font-smoothing: grayscale;
					content: "\f015";
					padding-right: 5px;
				}
			}
		}

		&::before,
		&::after{
			display: none;
		}

		a{
			padding: 0 15px;
			position: relative;
			z-index: 1;
			display: block;
			overflow: visible;
			max-width: none;
			text-decoration: none;
			color: inherit;
			margin-left: -15px;

			&:hover{
				color: xf-default(@xf-xtr_breadcrumbHover--color, inherit);
			}

			&::before,
			&::after {
				border-style: solid;
				border-width: 0;
				border-right-width: 1px;
				border-color: @xf-xtr_breadcrumbBorder--border-color;
				color: rgba(255, 255, 255, 0.38);
				box-shadow: inset currentColor -1px 0px 0px 0px;
				content: '';
				position: absolute;
				height: 50%;
				width: 100%;
				right: 0;
				z-index: -1;
				box-sizing: border-box;
				transform-origin: 100% 50%;
			}

			html[dir='rtl'] &:before,
			html[dir='rtl'] &:after{
				box-shadow: inset currentColor 1px 0px 0px 0px;
			}

			&::before {
				top: 0;
				transform: skewX(35deg);
			}

			&::after {
				top: 50%;
				transform: skewX(-35deg);
			}

			html[dir='rtl'] &:before{
				transform: skewX(-35deg);
			}

			html[dir='rtl'] &:after{
				transform: skewX(35deg);
			}

			/* Hover */
			&:hover::before,
			&:hover::after {
				background: @xf-xtr_breadcrumbHover--background-color;
			}

			/* Active */
			&:active::before,
			&:active::after{
				background-color: fade(@xf-xtr_breadcrumbHover--color, 3%);
				color: rgba(0,0,0,0.05);
				box-shadow: inset currentColor -2px 1px 1px;
			}

			&:active::after{
				box-shadow: inset currentColor -2px -1px 1px;
			}

            }

		&:first-of-type a::before,
		&:first-of-type a::after {
			width: ~"calc(100% + 20px)";
		}
	}
}

